//-- Add:
/**
* Second parse quote tag
* QPBL Style
*/
public function qpbl_bbcode_second_pass_quote($username, $post_id, $quote)
{
	global $cache, $config, $db, $user;
	global $phpEx;

	// Disabled?
	if (empty($config['qpbl_enable']))
	{
		return $this->bbcode_second_pass_quote($username, $quote);
	}

	// when using the /e modifier, preg_replace slashes double-quotes but does not
	// seem to slash anything else
	$quote = str_replace('\"', '"', $quote);
	$username = str_replace('\"', '"', $username);

	// Cache links so that we don't have to call append sid every time
	static $link_format = '';
	if (empty($link_format))
	{
		$link_format = append_sid(generate_board_url() . '/viewtopic.' . $phpEx, array('f' => '%3$d', 't' => '%2$d', 'p' => '%1$d', '#' => 'p%1$d'));
	}

	// remove newline at the beginning
	if ($quote == "\n")
	{
		$quote = '';
	}

	// See whether this post actually exists
	$qpbl_posts = $cache->get('_qpbl');
	if ($post_id && ($qpbl_posts === false || !isset($qpbl_posts[$post_id])))
	{
		$sql = 'SELECT post_id, topic_id, forum_id, post_subject, post_time
			FROM ' . POSTS_TABLE . '
			WHERE post_id = ' . (int) $post_id;
		$result	= $db->sql_query($sql);
		$data	= $db->sql_fetchrow($result);
		$db->sql_freeresult($result);

		if ($data !== false)
		{
			$qpbl_posts[$data['post_id']] = $data;

			// Cache it
			$cache->put('_qpbl', $qpbl_posts);
		}
	}

	// Prepare the information
	$replace = array();
	if ($username)
	{
		$replace['$1'] = $username;
	}

	if (!empty($qpbl_posts[$post_id]))
	{
		$replace['$2']		= vsprintf($link_format, $qpbl_posts[$post_id]);
		if ($config['qpbl_display'] == 'date')
		{
			// Need to replace the `L_IN` manually
			$replace[$user->lang('IN')]	= strtolower($user->lang('ON'));
			$replace['$3']	= $user->format_date($qpbl_posts[$post_id]['post_time']);
		}
		else
		{
			$replace['$3']	= censor_text($qpbl_posts[$post_id]['post_subject']);
		}

		// Force lowercase?
		if (!empty($config['qpbl_l_case']))
		{
			$replace['$3']	= strtolower($replace['$3']);
		}
	}

	// Select the correct template switch
	$tpl = '';
	if (sizeof($replace) >= 3)
	{
		$tpl = 'quote_username_post_open';
	}
	else if (empty($replace['$1']) && !empty($replace['$2']) && !empty($replace['$3']))
	{
		$tpl = 'quote_post_open';
	}
	else if (!empty($replace['$1']) && empty($replace['$2']) && empty($replace['$3']))
	{
		$tpl = 'quote_username_open';
	}
	else
	{
		return $this->bbcode_tpl('quote_open') . $quote;
	}

	return str_replace(array_keys($replace), $replace, $this->bbcode_tpl($tpl)) . $quote;
}
//-- End Quote Post Back Link

